import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import VoiceRecorder from "./components/VoiceRecorder";

const App: React.FC = () => {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState<{ user: string; bot: string }[]>([
    { user: "", bot: "Hello my skibidi master. How can I help you today?" },
  ]);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingDots, setLoadingDots] = useState(".");
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [transcribingDots, setTranscribingDots] = useState(".");
  const [isSpeechEnabled, setIsSpeechEnabled] = useState(true); // State to toggle speech synthesis

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isLoading) {
      interval = setInterval(() => {
        setLoadingDots((prev) => (prev === "..." ? "." : prev + "."));
      }, 500);
    }
    return () => clearInterval(interval);
  }, [isLoading]);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isTranscribing) {
      interval = setInterval(() => {
        setTranscribingDots((prev) => (prev === "..." ? "." : prev + "."));
      }, 500);
    }
    return () => clearInterval(interval);
  }, [isTranscribing]);

  const handleSendMessage = async () => {
    if (!message.trim()) return;

    const userMessage = message.trim();
    setMessage(""); 
    setChatHistory((prev) => [...prev, { user: userMessage, bot: loadingDots }]);
    setIsLoading(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/chatbot", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userMessage }),
      });
      const data = await response.json();

      setChatHistory((prev) => {
        const newHistory = [...prev];
        newHistory[newHistory.length - 1].bot = data.response;
        return newHistory;
      });

      if (isSpeechEnabled) {
        const speech = new SpeechSynthesisUtterance(data.response);
        window.speechSynthesis.speak(speech);
      }
    } catch (error) {
      console.error("Error:", error);
      setChatHistory((prev) => {
        const newHistory = [...prev];
        newHistory[newHistory.length - 1].bot =
          "Sorry, I encountered an error. Please try again.";
        return newHistory;
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleVoiceRecording = async (duration: number, transcript: string) => {
    const cleanTranscript = transcript.trim();
    setIsTranscribing(true);
    setChatHistory((prev) => [...prev, { user: `ðŸŽ¤ "${cleanTranscript}"`, bot: loadingDots }]);

    try {
      const response = await fetch("http://127.0.0.1:5000/chatbot", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: cleanTranscript, source: "voice_command" }),
      });
      const data = await response.json();

      setChatHistory((prev) => {
        const newHistory = [...prev];
        newHistory[newHistory.length - 1].bot = data.response;
        return newHistory;
      });

      if (isSpeechEnabled) {
        const speech = new SpeechSynthesisUtterance(data.response);
        window.speechSynthesis.speak(speech);
      }
    } catch (error) {
      console.error("Error:", error);
      setChatHistory((prev) => {
        const newHistory = [...prev];
        newHistory[newHistory.length - 1].bot =
          "Sorry, I encountered an error. Please try again.";
        return newHistory;
      });
    } finally {
      setIsTranscribing(false);
      setIsLoading(false);
    }
  };

  return (
    <div>
      <nav>
        {/* Navigation Links */}
      </nav>
      <div>
        <h1>Chatbot</h1>
        <div>
          <button onClick={() => setIsSpeechEnabled(!isSpeechEnabled)}>
            {isSpeechEnabled ? "Disable Voice" : "Enable Voice"}
          </button>
        </div>
        <div>
          {/* Chat History */}
        </div>
        <div>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
          />
          <button onClick={handleSendMessage}>Send</button>
          <VoiceRecorder onRecordingComplete={handleVoiceRecording} />
        </div>
      </div>
    </div>
  );
};

export default App;
