import React, { useState, useRef, useEffect } from 'react';

interface VoiceRecorderProps {
  onRecordingComplete: (duration: number) => void;
}

const VoiceRecorder: React.FC<VoiceRecorderProps> = ({ onRecordingComplete }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [currentFrame, setCurrentFrame] = useState(0); // 0 for initial mic.png
  const mediaRecorder = useRef<MediaRecorder | null>(null);
  const startTime = useRef<number>(0);
  const animationInterval = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (isRecording) {
      // Animate mic icon at 4 frames per second
      animationInterval.current = setInterval(() => {
        setCurrentFrame(prev => prev >= 4 ? 1 : prev + 1);
      }, 250); // 250ms = 4 frames per second
    } else {
      if (animationInterval.current) {
        clearInterval(animationInterval.current);
      }
      setCurrentFrame(0); // Reset to mic.png
    }

    return () => {
      if (animationInterval.current) {
        clearInterval(animationInterval.current);
      }
    };
  }, [isRecording]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorder.current = new MediaRecorder(stream);
      const audioChunks: Blob[] = [];

      mediaRecorder.current.ondataavailable = (event) => {
        audioChunks.push(event.data);
      };

      mediaRecorder.current.onstop = () => {
        const duration = (Date.now() - startTime.current) / 1000;
        const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
        onRecordingComplete(duration);
        
        stream.getTracks().forEach(track => track.stop());
      };

      startTime.current = Date.now();
      mediaRecorder.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error accessing microphone:', error);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder.current && isRecording) {
      mediaRecorder.current.stop();
      setIsRecording(false);
    }
  };

  return (
    <button
      onMouseDown={startRecording}
      onMouseUp={stopRecording}
      onMouseLeave={stopRecording}
      style={{
        padding: "0",
        borderRadius: "4px",
        backgroundColor: isRecording ? "#dc3545" : "#6c757d",
        border: "none",
        cursor: "pointer",
        transition: "background-color 0.3s ease",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "44px",
        height: "44px",
        overflow: "hidden"
      }}
    >
      <img 
        src={currentFrame === 0 ? "/mic.png" : `/mic${currentFrame}.png`}
        alt="Microphone"
        style={{ 
          width: "100%",
          height: "100%",
          padding: "8px",
          objectFit: "contain"
        }} 
      />
    </button>
  );
};

export default VoiceRecorder;
